$(document).ready(function(){$.getJSON("https://ipapi.co/json",function(e){var a=new Date,c=["January","February","March","April","May","June","July","August","September","October","November","December"],t=c[a.getMonth()+1],n=a.getDate(),r=a.getFullYear();$("#date").text(n+" "+t+" "+r);var s=e.latitude,o=e.longitude,d=e.city,y=e.country_name;$(".city").text(d+" , "+y);var i="https://api.darksky.net/forecast/142c5de8355ba633fe6f4ee1530d6c27/"+s+","+o;$.ajax({url:i,dataType:"jsonp",success:function(e){console.log("Hello Curious Developper :)");var a=Math.round(e.currently.temperature),c=Math.round((a-32)*(5/9));$(".degree-f").text(a),$(".degree-c").text(c),$(".tgl-btn").click(function(){"0px"===$(".degree-c").css("left")?($(".degree-f").animate({left:"0"},150),$(".degree-c").animate({left:"-100%"},150)):($(".degree-f").animate({left:"100%"},150),$(".degree-c").animate({left:"0"},150))});var t=e.currently.windSpeed;$(".wind > p").text(t);var n=e.currently.humidity;$(".humidity > p").text(n);var r=e.hourly.summary;$(".w-left > p:last-child").text(r);var s=e.currently.summary;$(".w-left > p:nth-child(2)").text(s);var o=e.currently.icon;switch(o){case"clear-day":skycons.add("icon1",Skycons.CLEAR_DAY);break;case"clear-night":skycons.add("icon1",Skycons.CLEAR_NIGHT);break;case"rain":skycons.add("icon1",Skycons.RAIN);break;case"snow":skycons.add("icon1",Skycons.SNOW);break;case"sleet":skycons.add("icon1",Skycons.SLEET);break;case"wind":skycons.add("icon1",Skycons.WIND);break;case"fog":skycons.add("icon1",Skycons.FOG);break;case"cloudy":skycons.add("icon1",Skycons.CLOUDY);break;case"partly-cloudy-day":skycons.add("icon1",Skycons.PARTLY_CLOUDY_DAY);break;case"partly-cloudy-night":skycons.add("icon1",Skycons.PARTLY_CLOUDY_NIGHT)}}})})});